import { Vector2 } from "../../../../index.js";
import TextureNode from "three/examples/jsm/nodes/accessors/TextureNode.js";
import Node from "three/examples/jsm/nodes/core/Node.js";
import TempNode from "three/examples/jsm/nodes/core/TempNode.js";
import { NodeRepresentation, ShaderNodeObject } from "../shadernode/ShaderNode.js";

export default class GaussianBlurNode extends TempNode {
    textureNode: TextureNode;
    sigma: number;

    directionNode: Node;

    resolution: Vector2;

    constructor(textureNode: TextureNode, sigma?: number);

    setSize(width: number, height: number): void;

    getTextureNode(): TextureNode;
}

export const gaussianBlur: (node: NodeRepresentation, sigma?: number) => ShaderNodeObject<GaussianBlurNode>;

declare module "../shadernode/ShaderNode.js" {
    interface NodeElements {
        gaussianBlur: typeof gaussianBlur;
    }
}
